	public boolean register(String login, String password, String name, String lastname, String email,
			String phonenumber, String type) {
		password = DigestUtils.sha1Hex(password);
		boolean odp = spbd.register(login, password, name, lastname, email, phonenumber, type);
		return odp;
	}

	boolean checkCharacters(String in, String sequence) {
		boolean wrong = true;
		String name = in.toLowerCase();
		for (char c : name.toCharArray()) {
			for (int i = 0; i < sequence.length(); i++) {
				if (c == sequence.charAt(i)) {
					wrong = false;
					break;
				}
			}
			if (wrong)
				return false;
		}
		return true;
	}

	boolean max(String in, int max) {
		if (in.length() > max)
			return false;
		return true;
	}

	boolean min(String in, int min) {
		if (in.length() < min)
			return false;
		return true;
	}

	boolean isPassowrdStrong(String password) {
		String digits = "1234567890";
		boolean hasUppercase = !password.equals(password.toLowerCase());
		boolean hasLowercase = !password.equals(password.toUpperCase());
		if (validation(password, digits) && hasUppercase && hasLowercase)
			return true;
		return false;
	}

	private boolean validation(String password, String sequence) {

		int i;
		for (char c : password.toCharArray()) {
			i = 0;
			while (i < sequence.length()) {
				if (sequence.charAt(i) == c)
					return true;
				i++;
			}
		}
		return false;
	}
